mapper.update:
UPDATE QNA
SET qanswer = #{qanswer}, qstatus = #{qstatus}
WHERE qno = #{qno}

mapper.insert:
INSERT INTO FAQ (fno, ftitle, fcontent) VALUES('F' ||
LPAD(seq_qna.NEXTVAL, 3, '0'),
#{ftitle}, #{fcontent}
)

INSERT INTO notice (nid, ntitle, ncontent, ndate)
VALUES('N' || LPAD(seq_notice.NEXTVAL, 3, '0'),
#{ntitle}, #{ncontent},
#{ndate}
)
